sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 * @param {ApiHelpers} params.helpers
 */
function handler({
    api,
    event,
    helpers,
    imports
}) {
    const {
        photoboothId,
        emailAddress
    } = api.state;

    const fields = `photobooth=${photoboothId}^email=${emailAddress}`;

    api.data.create_record.execute({
        "table": "x_snc_pb_core_request",
        "templateFields": fields
    });
}
