sys_ux_client_script.script:
/**
* @param {params} params
* @param {api} params.api
* @param {any} params.event
* @param {any} params.imports
* @param {ApiHelpers} params.helpers
*/
function handler({api : { setState, data, state }, event, helpers, imports}) {
    console.log("PHOTOBOOTH LOADED!")
    const pb = data.look_up_photobooths.results[0];

    setState("sysId", pb._row_data.uniqueValue);
    setState("selectedCameraDeviceId", [pb.camera_device_id.value]);
    setState("watermarkImage", pb.watermark_image.value);
    setState("watermarkImagePosition", pb.watermark_image_position.value);
    setState("watermarkImageHeight", pb.watermark_image_height.value);
    setState("gap", pb.gap.value);
    setState("fillStyle", pb.fill_style.value);
    setState("chinHeight", pb.chin_height.value);
    setState("name", pb.name.value);
    setState("longName", pb.long_name.value);

    data.look_up_request.refresh();
}
